INSERT INTO `article` VALUES ('1', '联系味美多', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: #868686; font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); line-height: 1.5em;&quot;&gt;&lt;span style=&quot;background-color: rgb(255, 255, 255);&quot;&gt;北京味多美食品有限责任公司&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: #868686; font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); line-height: 1.5em;&quot;&gt;&lt;span style=&quot;background-color: rgb(255, 255, 255);&quot;&gt;地址：北京市海淀区西直门北大街32号枫蓝国际中心B座7层&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: #868686; font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); line-height: 1.5em;&quot;&gt;&lt;span style=&quot;background-color: rgb(255, 255, 255);&quot;&gt;邮编：100082&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: #868686; font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); line-height: 1.5em;&quot;&gt;&lt;span style=&quot;background-color: rgb(255, 255, 255);&quot;&gt;电话：4001-170-170&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;');
INSERT INTO `article` VALUES ('3', '企业服务', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left; line-height: 1.5em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &quot;&gt;味多美创立于1996年，是国内著名烘焙连锁食品品牌，主要经营中高端蛋糕、面包、咖啡、中西式点心、月饼、粽子等产品。数十年来，我们围绕客户的需求持续创新，与合作伙伴开放合作，在企业员工福利关怀、聚会庆典、商务活动定制、客户关系维护等方面为众多知名企业客户提供了优质的外包服务解决方案。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 2em; text-align: left; line-height: 1.5em;&quot;&gt;&lt;span style=&quot;font-family: 微软雅黑, &quot;&gt;作为中国烘焙行业引领者，长期以来，味多美坚持以优质的服务、健康美味的产品，努力给消费者提供高品质的生活享受。与此同时，我们力争成为广大企业客户的第一选择和最佳合作伙伴，成为深受消费者钟爱。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;img src=&quot;/wmdCake/home/upload/20171010/1507619190419775.jpg&quot; title=&quot;1507619190419775.jpg&quot; alt=&quot;qyfw.jpg&quot;/&gt;&lt;/p&gt;');
INSERT INTO `article` VALUES ('4', '味美多简介', '&lt;p style=&quot;text-indent: 2em; line-height: 1.5em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; color: #858585&quot;&gt;北京味多美食品有限责任公司创立于1996年，是国内著名烘焙连锁食品企业，初期主要经营产品包括蛋糕、面包、咖啡、中西式点心、月饼、粽子等，经过多年来不断的发展与成长，目前产品的种类已扩展至味多美法式面包、饼干、老婆饼、蛋挞、芝士蛋糕、巧克力蛋糕、下午茶蛋糕、生日蛋糕、乳酪蛋糕以及婚礼蛋糕、商务庆典蛋糕。除了丰富多样的实体产品，更提供种类多样的味多美网上订购、团购、味多美福利卡、月饼卡、充值卡、提货卡，为广大消费者提供了多种消费选择。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 1.5em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; color: #858585;&quot;&gt;味多美成立至今，在中国已创办了300余家直营连锁店，2010—2011年相继落户上海、河北保定、包头等地。味多美在北京、上海、包头同时拥有现代化的中央工厂，集“生产”“配送”“销售”为一体，专业的品质管理制度，保证食品安全。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent: 2em; line-height: 1.5em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; color: #858585&quot;&gt;味多美品牌定位于中高端消费者，以丰富的、高品质的产品，平民化的价格，亲切友善的服务，便利的连锁店铺，赢得了消费者的青睐。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;');
INSERT INTO `article` VALUES ('5', '味美多工业园', '&lt;p style=&quot;line-height: normal; text-indent: 2em;&quot;&gt;&lt;span style=&quot;font-size: 14px; font-family: 微软雅黑, &amp;quot;Microsoft YaHei&amp;quot;; color: #858585;&quot;&gt;味多美中央工厂，坐落于北京市四大支柱产业之一，中关村科技园区大兴生物医药产业基地内。是占地40亩，按国际标准建造的大型食品工业园。&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; background-color: rgb(255, 255, 255);&quot;&gt;&lt;img src=&quot;/wmdCake/home/upload/20171010/1507620092212878.jpg&quot; title=&quot;1507620092212878.jpg&quot; alt=&quot;001.jpg&quot;/&gt;&lt;/span&gt;&lt;/p&gt;');
INSERT INTO `article` VALUES ('6', '味美多文化', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;定位：北京更受欢迎的面包坊&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;品类：法式面包坊&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;使命：让中国人吃到优质健康的面包&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;愿景：做中国最受欢迎的面包坊&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;企业计划：&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;2020年连锁店达到1000家；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;2040年连锁店达到10000家。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; color: rgb(255, 0, 0);&quot;&gt;s&amp;nbsp;企业精神： 工匠精神&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;价值观：认真、诚信、专注、追求完美和极致&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;企业目标：让每一个奋斗者在味多美实现自己的理想&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;员工目标：做个奋斗者，成为合伙人。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;奋斗口号：买车买房，接来爹娘。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;最重要的部门：门店&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;经营中心：&lt;span style=&quot;margin: 0px; padding: 0px; color: rgb(255, 0, 0);&quot;&gt;提升顾客满意度。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; color: rgb(255, 0, 0);&quot;&gt;s&amp;nbsp;管理文化：赛场文化。&lt;/span&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;管理方法：目标管理、过程管理。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;s&amp;nbsp;管理工具：人定岗、物定位、做流程、做标准、做记录、做培训、做考核、持续改善&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; font-size: smaller;&quot;&gt;&lt;br/&gt;&lt;/span&gt;&lt;br/&gt;&lt;/p&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;');
INSERT INTO `article` VALUES ('7', '关于原料', '&lt;p style=&quot;text-align: center;&quot;&gt;&lt;span style=&quot;color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; text-align: center; background-color: rgb(255, 255, 255);&quot;&gt;&amp;nbsp; &amp;nbsp; &amp;nbsp; &amp;nbsp;味多美坚持选用天然乳脂奶油、新鲜鸡蛋、高品质阳光小麦等多种健康食材，正是由于这些健康食材的选用，成就了味多美每一款产品的完美品质。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align: center;&quot;&gt;&lt;img src=&quot;/wmdCake/home/upload/20171010/1507620367884905.jpg&quot; title=&quot;1507620367884905.jpg&quot; alt=&quot;002(2)(1).jpg&quot;/&gt;&lt;/p&gt;');
