INSERT INTO `article` VALUES ('31', '运营主管（O2O配送方向）', '&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; color: rgb(51, 51, 51);&quot;&gt;岗位职责&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px;&quot;&gt;1、&lt;/span&gt;&lt;span style=&quot;color:#868686;font-family:微软雅黑, Verdana, Geneva, sans-serif&quot;&gt;负责公司自有O2O配送业务体系规划，业务运营管理及团队管理，包括流程、制度、操作手册与指标等；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color:#868686;font-family:微软雅黑, Verdana, Geneva, sans-serif&quot;&gt;2、负责第三方合作配送公司业务管理，监控订单配送状态、监督配送受限、服务质量、操作指标等；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color:#868686;font-family:微软雅黑, Verdana, Geneva, sans-serif&quot;&gt;3、负责处理配送过程中的各类问题及纠纷&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top: 0px; margin-bottom: 0px; padding: 0px; color: rgb(134, 134, 134); font-family: 微软雅黑, Verdana, Geneva, sans-serif; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);&quot;&gt;&lt;span style=&quot;color:#868686;font-family:微软雅黑, Verdana, Geneva, sans-serif&quot;&gt;4、结合实际业务情况，负责配送系统的规划和需求改进；&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin-top