-- ----------------------------
-- Table structure for goods_list
-- ----------------------------
DROP TABLE IF EXISTS `goods_list`;
CREATE TABLE `goods_list` (
  `id` int(10) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `cid` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '商品分类id',
  `name` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '商品名称',
  `img` varchar(2048) COLLATE utf8_unicode_ci NOT NULL COMMENT '商品图片',
  `depict` varchar(2048) COLLATE utf8_unicode_ci NOT NULL COMMENT '商品描述',
  `summary` varchar(2048) COLLATE utf8_unicode_ci NOT NULL COMMENT '商品提示',
  `price` decimal(10,0) NOT NULL COMMENT '价格',
  `pcs` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '单位',
  `likenum` int(50) NOT NULL COMMENT '喜欢的人数',
  `cnt` text COLLATE utf8_unicode_ci NOT NULL,
  `nums` int(10) NOT NULL,
  `prototype` varchar(2048) COLLATE utf8_unicode_ci NOT NULL,
  `remarks` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `type` int(10) NOT NULL,
  `sizeimg` varchar(255) COLLATE utf8_unicode_ci NOT NULL COMMENT '尺寸图',
  `pcssum` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `pricesum` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=51 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

